import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { subject } from '../_models/entityModels';

@Component({
  selector: 'app-reg-subject',
  templateUrl: './reg-subject.component.html',
  styleUrls: ['./reg-subject.component.css']
})
export class RegSubjectComponent implements OnInit{
  baseurl = "https://localhost:7217/api/"
  subject : subject | any = {};
  subjects : subject[] | any = {};
  valid=true;
  message = "Fields with * are Required";

  constructor(private http: HttpClient){}ngOnInit(): void {
    this.getSubjects();
  }

  getSubjects(){
    this.http.get(this.baseurl + 'subject/').subscribe({
      next : response => {
        this.subjects = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
    })
  }
;

  register(){
    this.validation();
    if(this.valid){
      //this.http.post<subject>(this.baseurl + "subject/register/" , this.subject).subscribe({});

    }

  }

  validation(){
    this.subjects.forEach((element : subject) => {
      if(this.subject.name == element.name){
        this.valid=false;
        this.message="Subject is already registred";
      }
    });

  }
}
<div class="container" style="margin: 35px 35px 35px 35px;">
    <div class="container" style="margin: 35px 35px 35px 35px;">
        <form #registerForm="ngForm" (ngSubmit)="register()" autocomplete="off">
            <h2 style="font-weight: bold; text-align: center;"> Subject Registration</h2>
            <hr>

            <div class="row">
                <div class="col-2">
                    <div style="margin-top: 10px;">
                        Subject Name 
                    </div>
                </div>
                <div class="col-10">
                    <div class="row">
                        <input  type="text" 
                            class="form-control" 
                            minlength="2"
                            [(ngModel)]="subject.name"
                            #firstName="ngModel"
                            name="FirstName"
                            placeholder="First Name">
                    </div>
                </div>
            </div>

            <div class="text-center" style="margin: 25px 25px 25px 25px;">
                <button class="btn btn-success me2" type="Submit" style="margin-right: 10px;"> Register Classroom </button>
                <button class="btn btn-secondary" routerLink="/" style="margin-left: 10px;"> Cancel </button>
            </div>

            <p style="color: red;">{{message}}</p>
            <p></p>
            <div></div>
        </form>
    </div>
</div>
