import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { classRoom, student, teacher } from '../_models/entityModels';

@Component({
  selector: 'app-report',
  templateUrl: './report.component.html',
  styleUrls: ['./report.component.css']
})
export class ReportComponent implements OnInit {
  baseurl = "https://localhost:7217/api/"
  students : student[] | any;
  classRoom : classRoom | any;
  teachers : teacher[] | any;

  classRoomID : number | any ;
  selectedStudentID : number | any;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {

    this.getStudents();
    this.getClassroom();
  }

  getStudents(){
    this.http.get(this.baseurl + 'student/').subscribe({
      next : response => {
        this.students = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })
  }

  getClassroom(){
    this.http.get(this.baseurl + 'classRoom/').subscribe({
      next : response => {
        this.classRoom = response;
        this.teachers = this.classRoom.teachers;
        console.log(this.classRoom);
        console.log(this.teachers);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })
  }

}
