import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { classRoom, student, subject, teacher } from './_models/entityModels';
import {MatSelectModule} from '@angular/material/select';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  classRooms : classRoom[] | any;
  students : student[] | any;
  subjects : subject[] | any;
  teachers : teacher[] | any;
  selectedClassRoom : string | any;
  selectedTeacher : string | any;



  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.http.get('https://localhost:7217/api/classRoom/').subscribe({
      next : response => {
        this.classRooms = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })

    this.http.get('https://localhost:7217/api/student/').subscribe({
      next : response => {
        this.students = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })

    this.http.get('https://localhost:7217/api/teacher/').subscribe({
      next : response => {
        this.teachers = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })

    this.http.get('https://localhost:7217/api/subject/').subscribe({
      next : response => {
        this.subjects = response;
        console.log(response);
      },
      error: () => console.log(console.error()),
      complete: () => console.log('Requse has Been Completed')
    })
  }



}
